import datetimestr = 'createtask 0-0 "Из Алисы в Кристину"\n' \      'repost 2438387968 2372416023'tasks = str.split('\n')first = tasks[0].split()n = ' '.join(first[1:]).split('"')if len(n) >= 2:    description = n[1]else:    description = Nonestart_date, end_date = n[0].split('-')end_date = end_date[:-1]if start_date == '0':    start_date = Noneelse:    start_date = datetime.datetime.strptime(start_date, "%d.%m.%Y %H:%M")if end_date == '0':    end_date = Noneelse:    end_date = datetime.datetime.strptime(end_date, "%d.%m.%Y %H:%M")print(description)print(tasks)